import React from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './HeroSection.module.css';
import heroImage from '../Assets/ClinicManagement.jpg';

function HeroSection({ onButtonClick }) {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token');

  const handleAgendarCita = () => {
    if (isAuthenticated) {
      navigate('/gestion-clinica');
    } else {
      navigate('/login', { state: { from: '/gestion-clinica' } });
    }
  };

  return (
    <section className={styles.hero} id="inicio">
      <div className={styles.heroContent}>
        <h2>Optimiza la gestión de tu clínica</h2>
        <p>
          Con HealthConnect, digitaliza la administración de tu clínica con herramientas eficientes para la gestión de citas, historiales médicos y pacientes.
        </p>
        <div className={styles.cta}>
          <button className={styles.primaryBtn} onClick={handleAgendarCita}>
            Explorar Plataforma
          </button>
          <button className={styles.secondaryBtn} onClick={() => navigate('/nosotros')}>
            Saber Más
          </button>
        </div>
      </div>
      <div className={styles.heroImage}>
        <img src={heroImage} alt="Gestión de clínicas" />
      </div>
    </section>
  );
}

export default HeroSection;
