import React from 'react';
import { useNavigate } from 'react-router-dom';
import styles from "./ServicesPreview.module.css";
import consultaIcon from '../Assets/Doctor4.jpg';
import laboratorioIcon from '../Assets/Doctor5.jpg';
import especialidadesIcon from '../Assets/Doctor6.jpg';

function ServicesPreview() {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token');

  const handleServiceClick = (serviceRoute) => {
    if (isAuthenticated) {
      navigate(`/servicios-clinicos${serviceRoute}`);
    } else {
      navigate('/login', { state: { from: `/servicios-clinicos${serviceRoute}` } });
    }
  };

  return (
    <section className={styles.services} id="servicios">
      <h2>Atención Médica de Calidad a tu Alcance</h2>
      <p className={styles.servicesIntro}>
        Brindamos una experiencia médica innovadora con servicios diseñados para tu bienestar y el de tu familia.
      </p>
      
      <div className={styles.servicesGrid}>
        <div className={styles.serviceCard}>
          <img src={consultaIcon} alt="Consulta médica" />
          <h3>Consulta General</h3>
          <p>Recibe atención profesional con especialistas que cuidan de tu salud en cada consulta.</p>
          <button onClick={() => handleServiceClick('/consulta-general')}>Más Información</button>
        </div>
        
        <div className={styles.serviceCard}>
          <img src={laboratorioIcon} alt="Laboratorio clínico" />
          <h3>Laboratorio Clínico</h3>
          <p>Resultados rápidos y precisos con tecnología avanzada en análisis clínicos.</p>
          <button onClick={() => handleServiceClick('/laboratorio')}>Más Información</button>
        </div>
        
        <div className={styles.serviceCard}>
          <img src={especialidadesIcon} alt="Especialidades médicas" />
          <h3>Especialidades Médicas</h3>
          <p>Accede a más de 20 especialidades con profesionales altamente capacitados.</p>
          <button onClick={() => handleServiceClick('/especialidades')}>Más Información</button>
        </div>
      </div>
      
      <div className={styles.servicesFooter}>
        <p>Conéctate con los mejores especialistas y agenda tu cita hoy mismo.</p>
        <button 
          className={styles.allServicesBtn}
          onClick={() => {
            if (isAuthenticated) {
              navigate('/servicios-clinicos');
            } else {
              navigate('/login', { state: { from: '/servicios-clinicos' } });
            }
          }}
        >
          Explorar Todos los Servicios
        </button>
      </div>
    </section>
  );
}

export default ServicesPreview;
