import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import styles from './ClinicAdminDashboard.module.css';

const ClinicAdminDashboard = () => {
  const [clinic, setClinic] = useState(null);
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [specialties, setSpecialties] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [isCreatingSpecialty, setIsCreatingSpecialty] = useState(false);
  const [newSpecialty, setNewSpecialty] = useState('');
  
  const [doctorForm, setDoctorForm] = useState({
    nombre: '',
    id_especialidad: '',
    horario_disponibles: ''
  });

  // Función para obtener todos los datos necesarios
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem('token');
        
        if (!token) {
          toast.error('No hay sesión activa. Por favor inicie sesión nuevamente.');
          setLoading(false);
          return;
        }

        const headers = {
          Authorization: `Bearer ${token}`
        };
        
        // Obtener información de la clínica
        try {
          const clinicRes = await axios.get('/api/clinic-admin/my-clinic', { headers });
          setClinic(clinicRes.data);
        } catch (error) {
          toast.error('No se pudo cargar la información de la clínica');
        }
        
        // Obtener médicos
        try {
          const doctorsRes = await axios.get('/api/clinic-admin/doctors', { headers });
          if (Array.isArray(doctorsRes.data)) {
            setDoctors(doctorsRes.data);
          } else {
            setDoctors([]);
          }
        } catch (error) {
          toast.error('No se pudo cargar la lista de médicos');
        }
        
        // Obtener especialidades
        try {
          const specialtiesRes = await axios.get('/api/specialties', { headers });
          if (Array.isArray(specialtiesRes.data)) {
            setSpecialties(specialtiesRes.data);
          } else {
            setSpecialties([]);
          }
        } catch (error) {
          toast.error('No se pudo cargar la lista de especialidades');
        }
        
      } catch (error) {
        toast.error('Error al cargar los datos. Por favor intente nuevamente.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, []);
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setDoctorForm({
      ...doctorForm,
      [name]: value
    });
  };
  
  const handleNewSpecialtyChange = (e) => {
    setNewSpecialty(e.target.value);
  };
  
  // Función para crear una nueva especialidad
  const handleCreateSpecialty = async () => {
    if (!newSpecialty.trim()) {
      toast.error('El nombre de la especialidad no puede estar vacío');
      return;
    }

    try {
      const response = await axios.post('/api/specialties', 
        { nombre: newSpecialty }, 
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      const newSpecialtyData = response.data.especialidad;
      setSpecialties([...specialties, newSpecialtyData]);
      setDoctorForm({
        ...doctorForm,
        id_especialidad: newSpecialtyData.id_especialidad
      });
      setNewSpecialty('');
      setIsCreatingSpecialty(false);
      toast.success('Especialidad creada exitosamente');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error al crear especialidad');
    }
  };
  
  const handleEditDoctor = (doctor) => {
    setSelectedDoctor(doctor);
    setDoctorForm({
      nombre: doctor.nombre || '',
      id_especialidad: doctor.id_especialidad || '',
      horario_disponibles: doctor.horario_disponibles || ''
    });
  };
  
  const handleCancelEdit = () => {
    setSelectedDoctor(null);
    setDoctorForm({
      nombre: '',
      id_especialidad: '',
      horario_disponibles: ''
    });
  };
  
  const handleSubmitDoctor = async (e) => {
    e.preventDefault();
    
    try {
      let response;
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };
      
      if (selectedDoctor) {
        response = await axios.put(
          `/api/clinic-admin/doctors/${selectedDoctor.id_medico}`, 
          doctorForm, 
          { headers }
        );
        
        setDoctors(doctors.map(doc => 
          doc.id_medico === selectedDoctor.id_medico ? response.data.medico : doc
        ));
        
        toast.success('Médico actualizado exitosamente');
      } else {
        response = await axios.post('/api/clinic-admin/doctors', doctorForm, { headers });
        const newDoctor = response.data.medico;
        
        if (newDoctor) {
          setDoctors([...doctors, newDoctor]);
          toast.success('Médico agregado exitosamente');
        } else {
          toast.error('Error en la respuesta del servidor');
        }
      }
      
      handleCancelEdit();
      
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error al guardar médico');
    }
  };
  
  const handleDeleteDoctor = async (id_medico) => {
    if (!window.confirm('¿Está seguro que desea eliminar este médico?')) return;
    
    try {
      await axios.delete(`/api/clinic-admin/doctors/${id_medico}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      setDoctors(doctors.filter(doc => doc.id_medico !== id_medico));
      toast.success('Médico eliminado exitosamente');
      
      if (selectedDoctor && selectedDoctor.id_medico === id_medico) {
        handleCancelEdit();
      }
    } catch (error) {
      if (error.response?.status === 400 && error.response?.data?.message?.includes('citas pendientes')) {
        toast.error('No se puede eliminar el médico porque tiene citas pendientes');
      } else {
        toast.error(error.response?.data?.message || 'Error al eliminar médico');
      }
    }
  };
  
  if (loading) return (
    <div className={styles.loader}>
      <div className={styles.loaderSpinner}></div>
    </div>
  );
  
  return (
    <div className={styles.dashboardContainer}>
      <h1 className={styles.dashboardTitle}>
        Dashboard Administrador - {clinic?.nombre || 'Clínica'}
      </h1>
      
      <div className="row mb-4">
        <div className="col-md-4">
          <div className={styles.clinicInfoCard}>
            <div className={styles.clinicInfoHeader}>
              <h5 className="mb-0">Información de la Clínica</h5>
            </div>
            <div className={styles.clinicInfoBody}>
              {clinic ? (
                <>
                  <p className={styles.clinicInfoItem}>
                    <span className={styles.clinicInfoLabel}>Nombre:</span> {clinic.nombre}
                  </p>
                  <p className={styles.clinicInfoItem}>
                    <span className={styles.clinicInfoLabel}>Tipo:</span> {clinic.tipo}
                  </p>
                  <p className={styles.clinicInfoItem}>
                    <span className={styles.clinicInfoLabel}>Dirección:</span> {clinic.direccion}
                  </p>
                  <p className={styles.clinicInfoItem}>
                    <span className={styles.clinicInfoLabel}>Teléfono:</span> {clinic.telefono}
                  </p>
                  <p className={styles.clinicInfoItem}>
                    <span className={styles.clinicInfoLabel}>Email:</span> {clinic.email}
                  </p>
                </>
              ) : (
                <p className={styles.emptyMessage}>No se ha podido cargar la información de la clínica</p>
              )}
            </div>
          </div>
        </div>
        
        <div className="col-md-8">
          <div className={styles.doctorFormCard}>
            <div className={styles.doctorFormHeader}>
              <h5 className="mb-0">
                {selectedDoctor ? 'Editar Médico' : 'Agregar Médico'}
              </h5>
            </div>
            <div className={styles.doctorFormBody}>
              <form onSubmit={handleSubmitDoctor}>
                <div className={styles.formGroup}>
                  <label htmlFor="nombre" className={styles.formLabel}>Nombre del Médico</label>
                  <input
                    type="text"
                    className={styles.formControl}
                    id="nombre"
                    name="nombre"
                    value={doctorForm.nombre}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                
                <div className={styles.formGroup}>
                  <label className={styles.formLabel}>Especialidad</label>
                  {isCreatingSpecialty ? (
                    <div className={styles.newSpecialtyContainer}>
                      <input
                        type="text"
                        className={styles.formControl}
                        value={newSpecialty}
                        onChange={handleNewSpecialtyChange}
                        placeholder="Nombre de la nueva especialidad"
                        required
                      />
                      <div className={styles.specialtyButtonGroup}>
                        <button 
                          type="button" 
                          className={styles.submitButton}
                          onClick={handleCreateSpecialty}
                        >
                          Guardar
                        </button>
                        <button 
                          type="button" 
                          className={styles.cancelButton}
                          onClick={() => setIsCreatingSpecialty(false)}
                        >
                          Cancelar
                        </button>
                      </div>
                    </div>
                  ) : (
                    <div className={styles.specialtySelectContainer}>
                      <select
                        className={styles.formControl}
                        id="id_especialidad"
                        name="id_especialidad"
                        value={doctorForm.id_especialidad}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Seleccionar especialidad</option>
                        {specialties.map(specialty => (
                          <option 
                            key={specialty.id_especialidad} 
                            value={specialty.id_especialidad}
                          >
                            {specialty.nombre}
                          </option>
                        ))}
                      </select>
                      <button 
                        type="button" 
                        className={styles.addSpecialtyButton}
                        onClick={() => setIsCreatingSpecialty(true)}
                      >
                        + Nueva Especialidad
                      </button>
                    </div>
                  )}
                </div>
                
                <div className={styles.formGroup}>
                  <label htmlFor="horario_disponibles" className={styles.formLabel}>
                    Horario Disponible
                  </label>
                  <input
                    type="text"
                    className={styles.formControl}
                    id="horario_disponibles"
                    name="horario_disponibles"
                    value={doctorForm.horario_disponibles}
                    onChange={handleInputChange}
                    placeholder="Ej: Lunes-Viernes 8:00-16:00"
                    required
                  />
                </div>
                
                <div className={styles.buttonGroup}>
                  <button type="submit" className={styles.submitButton}>
                    {selectedDoctor ? 'Actualizar Médico' : 'Agregar Médico'}
                  </button>
                  {selectedDoctor && (
                    <button 
                      type="button" 
                      className={styles.cancelButton}
                      onClick={handleCancelEdit}
                    >
                      Cancelar
                    </button>
                  )}
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
      
      <div className={styles.doctorsTableCard}>
        <div className={styles.doctorsTableHeader}>
          <h5 className="mb-0">Médicos de la Clínica</h5>
        </div>
        <div className={styles.doctorsTableBody}>
          <div className="table-responsive">
            <table className={styles.doctorsTable}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nombre</th>
                  <th>Especialidad</th>
                  <th>Horario</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {doctors.length > 0 ? (
                  doctors.map(doctor => (
                    <tr key={doctor.id_medico}>
                      <td>{doctor.id_medico}</td>
                      <td>{doctor.nombre}</td>
                      <td>{doctor.especialidad}</td>
                      <td>{doctor.horario_disponibles}</td>
                      <td className={styles.actionButtons}>
                        <button 
                          className={styles.editButton}
                          onClick={() => handleEditDoctor(doctor)}
                        >
                          Editar
                        </button>
                        <button 
                          className={styles.deleteButton}
                          onClick={() => handleDeleteDoctor(doctor.id_medico)}
                        >
                          Eliminar
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className={styles.emptyMessage}>
                      No hay médicos registrados
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClinicAdminDashboard;
