import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styles from '../NavBar.module.css';

const NavBar = () => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token');
  const userRole = localStorage.getItem('userRole');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    navigate('/login');
  };

  return (
    <nav className={styles.navbar}>
      <Link to="/" className={styles.navLink}>Inicio</Link>
      {isAuthenticated && userRole === '1' && (
        <Link to="/admin-dashboard" className={styles.navLink}>Dashboard Admin</Link>
      )}
      {isAuthenticated && userRole !== '1' && (
        <Link to="/servicios-clinicos" className={styles.navLink}>Servicios Clínicos</Link>
      )}
      {isAuthenticated ? (
        <button onClick={handleLogout} className={styles.navButton}>Cerrar Sesión</button>
      ) : (
        <>
          <Link to="/login" className={styles.navLink}>Iniciar Sesión</Link>
          <Link to="/register" className={styles.navLink}>Registrarse</Link>
        </>
      )}
    </nav>
  );
};

export default NavBar;
