// src/pages/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from '../components/Login.module.css';


const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5002/api/auth/login', {
                username,
                contrasena: password
            });
            localStorage.setItem('token', res.data.token);
            navigate('/');
        } catch (err) {
            alert('Error al iniciar sesión');
        }
    };

    const handleRegisterRedirect = () => {
        navigate('/register');
    };

    return (
        <div className={styles.container}>
            <div className={styles.imageContainer}>
                <img src={medicalImage} alt="Login" className={styles.image}/>
            </div>
            <form className={styles.form} onSubmit={handleSubmit}>
                <h2 className={styles.title}>Iniciar Sesión</h2>
                <input
                    type="text"
                    placeholder="Nombre de usuario"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className={styles.input}
                    required
                />
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className={styles.input}
                    required
                />
                <button type="submit" className={styles.button}>Iniciar Sesión</button>
                <button type="button" className={styles.registerButton} onClick={handleRegisterRedirect}>Registrarse</button>
            </form>
        </div>
    );
};

export default Login;
